import React from 'react';

import Header from './components/Header';
import Categories from './components/Categories';
import Sort from './components/Sort';
import PizzaBlock from './components/PizzaBlock';

import './scss/app.scss';


function App() {
  return (
    <div class="wrapper">
      <Header />
      <div class="content">
        <div class="container">
          <div class="content__top">
            <Categories />
            <Sort/>
          </div>
          <h2 class="content__title">Все пиццы</h2>
          <div class="content__items">
            <PizzaBlock title="Мексиканская" price="500" img="https://cdn.carte.by/assets/2020/01/14/9296f61e5dc9e4c8df088c942113133d-d047c_src---jpg_710x_83b40_convert.jpg"/>
            <PizzaBlock title="Шашлычанская" price="300" img="https://img3.zakaz.ua/upload.version_1.0.b4ef4dae104e843a626b743ee99bbdf0.1350x1350.jpeg"/> 
            <PizzaBlock title="Киевская" price="350" img="https://eco-pizza.ru/image/cache/catalog/pizza/png/PEPPERONI-500x480.png" /> 
            <PizzaBlock title="Байрактар" price="400" img="https://pizzapapas.ru/image/cache/catalog/1pizza/papaspiz-500x500.jpg" /> 
            <PizzaBlock title="ЗСУ" price="450" img="https://static.tildacdn.com/tild6338-6366-4366-b331-623232376330/pizza18.jpg" /> 
            <PizzaBlock title="Азовсталь" price="560" img="https://eco-pizza.ru/moskovskiy/image/cache/catalog/pizza/png/MEKSIKA-494x500.png" /> 
            <PizzaBlock title="Перемога" price="570" img="https://delight.pizza/cache/images/Pizza-4-sezona/Pizza-4-sezona_600x600.jpg" />
            <PizzaBlock title="Українська" price="580" />
            <PizzaBlock title="Чотири сири" price="250" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
