import React from 'react';
function Categories() {
	const [activeIndex, setActiveIndex] = React.useState();
	return (
		<div className="categories">
			<ul>
				<li>Все</li>
				<li>Мясные</li>
				<li>Вегетарианская</li>
				<li className={activeIndex === 3 ? 'active' : ''}>Гриль</li>
				<li>Острые</li>
				<li>Закрытые</li>
			</ul>
		</div>
	);
}
export default Categories;
